resultMap.select:
select match_play_id,changci_id,play_content,play_type,status,is_del,create_time,update_time
from dl_match_play
where is_del = 0 and status=0 

 	and changci_id in
 	
 		#{changciId}
 	
  
 
 	and play_type = ${playType}

select dm.match_play_id matchPlayId, dm.changci_id changciId, dm.play_content playContent, dm.play_type playType, dm.status status, dm.is_del isDel, dm.is_hot isHot, dm.create_time createTime, dm.update_time updateTime, dml.match_live_info matchLiveInfo from dl_match_play dm left join dl_match_live dml on dm.changci_id = dml.changci_id where dm.is_del = 0 and dm.status=0
and dm.changci_id in
#{changciId}
and dm.play_type = ${playType}

resultMap.update:
update dl_match_play
set play_content = #{playContent},update_time=#{updateTime}
where changci_id=#{changciId} and play_type=#{playType}
